import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function HeroSection() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    pauseOnHover: false,
    arrows: false,
    cssEase: "ease-in-out",
  };

  const slides = [
    {
      img: "/images/hero1.jpg",
      title: "Health Doesn’t Wait — Now Neither Should You!",
      subtitle: "Reliable Medical Solutions for Every Need.",
      cta: "Shop Now",
    },
    {
      img: "/images/hero2.jpg",
      title: "Advanced Care. Trusted Technology.",
      subtitle: "Bringing precision and comfort to modern healthcare.",
      cta: "Explore Products",
    },
    {
      img: "/images/hero3.jpg",
      title: "EmbleMed Care",
      subtitle: "Health Devices You Can Count On — Every Day.",
      cta: "Contact Us",
    },
  ];

  return (
    <div className="relative mt-0 w-full">
      <Slider {...settings}>
        {slides.map((slide, i) => (
          <div
            key={i}
            className="relative w-full h-[55vh] sm:h-[65vh] md:h-[80vh] lg:h-[85vh] overflow-hidden"
          >
            {/* ✅ Image */}
            <img
              src={slide.img}
              alt={`Slide ${i + 1}`}
              className="w-full h-full object-contain sm:object-cover object-center md:object-[50%_30%] transition-all duration-700"
            />

            {/* ✅ Overlay gradient for better text visibility */}
            <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-black/20 to-transparent"></div>

            {/* ✅ Caption */}
            <div className="absolute inset-0 flex flex-col items-center justify-center text-center text-white px-4">
              <div className="animate-fade-in-up">
                <h2 className="text-2xl sm:text-3xl md:text-5xl font-bold drop-shadow-lg leading-snug">
                  {slide.title}
                </h2>
                <p className="mt-3 text-sm sm:text-lg md:text-xl opacity-90">
                  {slide.subtitle}
                </p>
                <button className="mt-5 bg-brand hover:bg-brand-dark text-white font-medium px-5 py-2 rounded-full shadow-brand transition-all duration-300">
                  {slide.cta}
                </button>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
}
