import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import logo from "../assets/logo.png";

export default function Navbar() {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 30);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <nav
      className={`fixed w-full top-0 left-0 z-50 transition-all duration-300 ${
        isScrolled ? "bg-white shadow-lg py-2" : "bg-white py-4 shadow-md"
      }`}
    >
      <div className="max-w-7xl mx-auto flex justify-between items-center px-6 transition-all duration-300">

        {/* ✅ Logo Section */}
        <Link to="/" className="flex items-center gap-3 group">
          <img
            src={logo}
            alt="EmbleMed Care Logo"
            className={`transition-all duration-300 ease-in-out ${
              isScrolled ? "w-10 h-10" : "w-14 h-14"
            } object-contain group-hover:scale-110`}
          />
          <div
            className={`font-semibold transition-all duration-300 ${
              isScrolled
                ? "text-lg text-gray-800"
                : "text-2xl text-gray-800"
            } group-hover:text-blue-600`}
          >
            EmbleMed Care
          </div>
        </Link>

        {/* ✅ Navigation Links */}
        <ul className="hidden md:flex space-x-10 font-medium">
          {[
            { name: "Home", path: "/" },
            { name: "About Us", path: "/about" },
            { name: "Products", path: "/products" },
            { name: "Contact Us", path: "/contact" },
          ].map((link) => (
            <li key={link.name}>
              <Link
                to={link.path}
                className="text-gray-700 hover:text-blue-600 transition-colors duration-200 relative after:absolute after:left-0 after:-bottom-1 after:h-[2px] after:w-0 after:bg-blue-600 hover:after:w-full after:transition-all after:duration-300"
              >
                {link.name}
              </Link>
            </li>
          ))}
        </ul>

        {/* ✅ Mobile Menu Icon */}
        <div className="md:hidden">
          <button aria-label="menu" className="text-gray-700 text-3xl">
            ☰
          </button>
        </div>
      </div>
    </nav>
  );
}
