import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function HeroSection() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false,
  };

  const slides = ["/images/hero1.jpg", "/images/hero2.jpg", "/images/hero3.jpg"];

  return (
    <div className="relative mt-0 w-full overflow-hidden">
      <Slider {...settings}>
        {slides.map((s, i) => (
          <div key={i} className="relative w-full">
            {/* ✅ Fix: Use object-contain for full visibility */}
            <img
              src={s}
              alt={`slide-${i}`}
              className="w-full h-[60vh] md:h-[80vh] object-contain md:object-fill bg-black"
              style={{
                objectPosition: "center",
              }}
            />

            {/* ✅ Optional: Overlay gradient for better text contrast */}
            <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent" />

            {/* ✅ Optional: Text overlay (you can customize this later) */}
            <div className="absolute inset-0 flex items-center justify-center text-center text-white px-4">
              {/* Example content placeholder */}
              {/* <div>
                <h1 className="text-3xl md:text-5xl font-bold mb-3">
                  Welcome to EmbleMed Care
                </h1>
                <p className="text-lg md:text-xl">
                  Delivering trusted medical innovation for better healthcare.
                </p>
              </div> */}
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
}
