import React, { useEffect, useState } from "react";
import {
  FaWhatsapp,
  FaEnvelope,
  FaPhone,
  FaInstagram,
  FaLinkedin,
} from "react-icons/fa";

export default function ContactFloat() {
  const phone = "+919987120563";
  const instagramURL = "https://www.instagram.com/emblemedcare";
  const linkedinURL = "https://www.linkedin.com/company/emblemedcare";

  const [isVisible, setIsVisible] = useState(false);

  // ðŸ‘‡ Show icons when user scrolls down a bit
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) setIsVisible(true);
      else setIsVisible(false);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div
      className={`hidden md:flex fixed right-4 bottom-6 flex-col gap-3 z-50 transition-all duration-700 ease-out ${
        isVisible ? "translate-x-0 opacity-100" : "translate-x-24 opacity-0"
      }`}
    >
      {/* WhatsApp */}
      <a
        href={`https://wa.me/${phone.replace(/\D/g, "")}`}
        target="_blank"
        rel="noreferrer"
        className={`w-12 h-12 rounded-full bg-green-500 flex items-center justify-center text-white shadow-lg hover:scale-110 transition-all duration-500 ease-out ${
          isVisible ? "animate-bounce-in delay-[0ms]" : ""
        }`}
        title="Chat on WhatsApp"
      >
        <FaWhatsapp size={22} />
      </a>

      {/* Email */}
      <a
        href="mailto:care.emblemedcare@gmail.com"
        className={`w-12 h-12 rounded-full bg-gray-700 flex items-center justify-center text-white shadow-lg hover:scale-110 transition-all duration-500 ease-out ${
          isVisible ? "animate-bounce-in delay-[150ms]" : ""
        }`}
        title="Send an Email"
      >
        <FaEnvelope size={20} />
      </a>

      {/* Call */}
      <a
        href={`tel:${phone}`}
        className={`w-12 h-12 rounded-full bg-blue-600 flex items-center justify-center text-white shadow-lg hover:scale-110 transition-all duration-500 ease-out ${
          isVisible ? "animate-bounce-in delay-[300ms]" : ""
        }`}
        title="Call Us"
      >
        <FaPhone size={20} />
      </a>

      {/* Instagram */}
      <a
        href={instagramURL}
        target="_blank"
        rel="noreferrer"
        className={`w-12 h-12 rounded-full bg-pink-500 flex items-center justify-center text-white shadow-lg hover:scale-110 transition-all duration-500 ease-out ${
          isVisible ? "animate-bounce-in delay-[450ms]" : ""
        }`}
        title="Visit Instagram"
      >
        <FaInstagram size={22} />
      </a>

      {/* LinkedIn */}
      <a
        href={linkedinURL}
        target="_blank"
        rel="noreferrer"
        className={`w-12 h-12 rounded-full bg-blue-800 flex items-center justify-center text-white shadow-lg hover:scale-110 transition-all duration-500 ease-out ${
          isVisible ? "animate-bounce-in delay-[600ms]" : ""
        }`}
        title="Visit LinkedIn"
      >
        <FaLinkedin size={22} />
      </a>
    </div>
  );
}
