import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function HeroSection(){
  const settings = { dots: true, infinite: true, speed: 600, slidesToShow:1, slidesToScroll:1, autoplay:true, autoplaySpeed:3000 };
  const slides = ['/images/hero1.jpg', '/images/hero2.jpg', '/images/hero3.jp'];
  return (
    <div className="mt-0">
      <Slider {...settings}>
        {slides.map((s,i)=>(
          <div key={i} className="h-[60vh] md:h-[70vh] flex items-center justify-center bg-gray-50">
            <img src={s} alt="hero" className="w-full h-full object-cover" />
            <div className="absolute inset-0 flex items-center">
              <div className="max-w-2xl mx-auto text-center p-4">
                <h2 className="text-3xl md:text-5xl font-bold text-gray-800">EmbleMed Care</h2>
                <p className="mt-3 text-gray-600">Reliable medical equipment for hospitals and clinics.</p>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
}
