import React from 'react';

export default function AboutSection(){
  return (
    <section className="py-16 px-4 max-w-6xl mx-auto bg-white">
      <div className="md:flex md:items-center md:gap-8">
        <div className="md:w-1/2">
          <h2 className="text-3xl font-semibold text-gray-800">About EmbleMed Care</h2>
          <p className="mt-4 text-gray-600">EmbleMed Care provides thoughtfully designed medical equipment that suits hospitals, clinics, and home-care providers. We focus on reliability, clear documentation, and strong after-sales support.</p>
        </div>
        <div className="md:w-1/2 mt-6 md:mt-0">
          <img src="/images/hero2.svg" alt="about" className="rounded shadow" />
        </div>
      </div>
    </section>
  );
}
